import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from 'react-leaflet';

const API = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';

function App() {
  const [form, setForm] = useState({ species: '', quantity: 1, lat: '', lon: '' });
  const [catches, setCatches] = useState([]);
  const [hotspots, setHotspots] = useState([]);
  const [loading, setLoading] = useState(false);

  async function fetchCatches() {
    try {
      const res = await axios.get(`${API}/catches/`);
      setCatches(res.data || []);
    } catch (err) {
      console.error('fetchCatches error', err);
    }
  }

  async function fetchHotspots() {
    try {
      const res = await axios.get(`${API}/hotspots/`);
      setHotspots(res.data || []);
    } catch (err) {
      console.error('fetchHotspots error', err);
    }
  }

  useEffect(() => {
    fetchCatches();
    fetchHotspots();

    // get geolocation for convenience
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition((pos) => {
        setForm((f) => ({ ...f, lat: pos.coords.latitude.toFixed(6), lon: pos.coords.longitude.toFixed(6) }));
      }, (err) => {
        // ignore
      });
    }
  }, []);

  async function submitCatch(e) {
    e.preventDefault();
    if (!form.species || !form.quantity || !form.lat || !form.lon) {
      alert('Please fill all fields');
      return;
    }
    setLoading(true);
    try {
      await axios.post(`${API}/catches/`, { ...form, fisherman_id: 1 });
      setForm({ species: '', quantity: 1, lat: form.lat, lon: form.lon });
      await fetchCatches();
      await fetchHotspots();
    } catch (err) {
      console.error('submitCatch error', err);
      alert('Error saving catch (see console)');
    } finally {
      setLoading(false);
    }
  }

  // derive map centre
  const center = catches.length > 0 ? [catches[0].lat, catches[0].lon] : (hotspots.length>0 ? [hotspots[0].lat, hotspots[0].lon] : [11.0, 79.8]);

  return (
    <div>
      <h1>BioCatch ‚Äî MVP</h1>
      <div className="app-grid">
        <div className="form-card">
          <h3>Log Catch</h3>
          <form onSubmit={submitCatch}>
            <div>
              <label>Species:</label><br />
              <input value={form.species} onChange={(e) => setForm({ ...form, species: e.target.value })} required />
            </div>
            <div>
              <label>Quantity:</label><br />
              <input type="number" value={form.quantity} onChange={(e) => setForm({ ...form, quantity: parseInt(e.target.value) })} required />
            </div>
            <div>
              <label>Latitude:</label><br />
              <input value={form.lat} onChange={(e) => setForm({ ...form, lat: parseFloat(e.target.value) })} required />
            </div>
            <div>
              <label>Longitude:</label><br />
              <input value={form.lon} onChange={(e) => setForm({ ...form, lon: parseFloat(e.target.value) })} required />
            </div>
            <div style={{ marginTop: 8 }}>
              <button type="submit" disabled={loading}>{loading ? 'Saving...' : 'Submit'}</button>
            </div>
          </form>

          <h4 style={{ marginTop: 18 }}>Recent Catches</h4>
          <div className="list-small">
            <ul>
              {catches.map(c => (
                <li key={c.id}>
                  üêü <b>{c.species}</b> √ó{c.quantity} ‚Äî ({Number(c.lat).toFixed(4)}, {Number(c.lon).toFixed(4)})<br/>
                  <small>{new Date(c.created_at).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          </div>

          <h4 style={{ marginTop: 12 }}>Hotspots Summary</h4>
          <div className="list-small">
            <ul>
              {hotspots.map((h, i) => (
                <li key={i}>
                  üåä <b>{h.species}</b> ‚Äî ({Number(h.lat).toFixed(3)}, {Number(h.lon).toFixed(3)}) ‚Äî Count: {h.count} ‚Äî Prob: {Math.round(h.prob * 100)}%
                </li>
              ))}
            </ul>
          </div>
        </div>

        <div>
          <h3>Map</h3>
          <div className="map-container" id="mapRoot" style={{ height: 600 }}>
            <MapContainer center={center} zoom={8} style={{ height: '100%', width: '100%' }}>
              <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="&copy; OpenStreetMap contributors"
              />
              {catches.map(c => (
                <Marker key={"c"+c.id} position={[c.lat, c.lon]}>
                  <Popup>
                    <b>{c.species}</b> √ó{c.quantity}<br/>
                    ({Number(c.lat).toFixed(4)}, {Number(c.lon).toFixed(4)})
                  </Popup>
                </Marker>
              ))}
              {hotspots.map((h, i) => (
                <CircleMarker
                  key={"h"+i}
                  center={[h.lat, h.lon]}
                  radius={6 + Math.min(12, h.count)}
                  pathOptions={{ color: h.prob > 0.5 ? 'red' : 'orange' }}
                >
                  <Popup>
                    <b>{h.species}</b> ‚Äî Prob: {Math.round(h.prob * 100)}% ‚Äî Count: {h.count}
                  </Popup>
                </CircleMarker>
              ))}
            </MapContainer>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
